import React from 'react'


class Spiner extends React.Component{

    render(){
        return <div className="m-5 d-flex justify-content-center">{ this.props.state ?
            <div className="loader" style = {this.props.color ? {borderTopColor : "#f76fd9"}: null}></div> : null}
        </div>
    }

}

export default Spiner
