import React from 'react'


class StatisticCard extends React.Component{
    constructor(props){
        super(props)
        // this callback will set the amount in the state object
        this.props.callback(this)
        
    }
    render(){
        return <div className={this.props.className+" card m-2 "} style={{width: '15rem',padding:"5px"}}>
                    <div className="card-body " style={{padding:"5px"}}>
                        <h5 className="card-title h4">{this.props.titleVar}</h5>
                        <p className="card-text font">{this.state.amount} {this.props.icon}</p>
                        <p className="card-text fontSmall">{this.props.text}</p>
                    </div>
            </div>
    }

}

export default StatisticCard
