import React from 'react'
import CooperativeRow from './CooperativeRow'
class CooperativesList extends React.Component{
  constructor(props){
    super(props)
    this.state = {
      currentSecteur: props.defaultSecteur
    }
    
  }
  createCoopRow(props, cooperative, color){
            return<CooperativeRow 
              flyToCooperative = {this.props.flyToCooperative}
              onSelectedCooperativeChange = {props.onSelectedCooperativeChange}
              onHoveredCooperativeChange = {props.onHoveredCooperativeChange}
              key={cooperative._id} 
              num = {cooperative.num}
              cooperative = {cooperative}
              color = {color}>{cooperative.nom_coop}</CooperativeRow>
  }
  render(){
      let rows = []
      const currentSecteur = parseInt(this.props.currentSecteur)
      if(currentSecteur!==-1){
        const cooperatives = this.props.coopData[currentSecteur].cooperatives
        const color = this.props.coopData[currentSecteur].color
        cooperatives.map((cooperative)=>{
          rows.push(this.createCoopRow(this.props, cooperative, color))
          return null
        })
      }
      else{
        this.props.coopData.map((secteur)=>{
          const color = secteur.color
          secteur.cooperatives.map((cooperative)=>{
            rows.push(this.createCoopRow(this.props, cooperative, color))
            return null
          })
        
        return null})
      }
      return <div className="coopList">
                <ul className="list-group">
                    {rows}
                </ul>
        </div>
  }
}

export default CooperativesList
