import React from 'react'
import AnnocReactMap from './AnnocReactMap'
import Navbar from '../Navbar/Navbar'
import TopStatistiCView from '../StatisticViews/TopStatistiCView'
import StatisticCard from '../StatisticViews/StatisticCard'
import axios from 'axios';
import CooperativeStatistics from "../StatisticViews/CooperativeStatistics";


class AnnocRootApp extends React.Component{
    constructor(props){
        super(props)
        this.state ={currentSecteur: -1,
                    currentSelectedCooperative : null,
                    TotalOfEleveur: "...",
                    TotalOfEspece:"...",
                    totalOfAnimateur:"...",
                    totalOfTechnicien:"..."}
        this.onSelectedSecteurChange = this.onSelectedSecteurChange.bind(this)
        this.onSelectedCooperativeChange = this.onSelectedCooperativeChange.bind(this)
        this.TotalSec = 0
        this.TotalCoop = 0
        this.calculOfTotalSec()
        this.calculOfTotalCoop()
        this.setTotalCoop = this.setTotalCoop.bind(this)
        this.setTotalSec = this.setTotalSec.bind(this)
    }
    onSelectedSecteurChange(currentSecteur){
        this.setState({currentSecteur})
      }
    onSelectedCooperativeChange(currentSelectedCooperative){
        this.setState({currentSelectedCooperative})
      }
    calculOfTotalSec(){
        this.TotalSec = this.props.coopData.length
    }
    calculOfTotalEspece(component){
        let queryTotalEspece= "http://127.0.0.1:8000/api/dashprovider/totalespeces"
        component.state = {amount: 0}
        axios.get(queryTotalEspece).then(res=>{
            component.setState({amount:res.data})
        })
    }
    calculOfTotalEleveur(component){
        let queryTotalAllEleveur = "http://127.0.0.1:8000/api/eleveurs"
        component.state = {amount: 0}
        axios.get(queryTotalAllEleveur).then(res=>{
            component.setState({amount:res.data.length})
        })
    }
    calculOfTotalTechniciens(component){
        let queryTechnicien = "http://127.0.0.1:8000/api/dashprovider/TotalOftechniciens"
        component.state = {amount: 0}
        axios.get(queryTechnicien).then(res=>{
            component.setState({amount:res.data})
        })
    }
    calculOfTotalAnimateur(component){
        let queryTotalAnimateur = "http://127.0.0.1:8000/api/dashprovider/totalanimateurs"
        component.state = {amount: 0}
        axios.get(queryTotalAnimateur).then(res=>{
            component.setState({amount:res.data})
        })
    }
    calculOfTotalConsommateurs(component){
        let queryTotalConsommateur = "http://127.0.0.1:8000/api/dashprovider/TotalOfConsommateurs"
        component.state = {amount: 0}
        axios.get(queryTotalConsommateur).then(res=>{
            component.setState({amount:res.data})
        })
    }
    calculOfTotalCoop(){
        let totcop = 0
        this.props.coopData.map((sec)=>{
            totcop += sec.cooperatives.length
            return null
        })
        this.TotalCoop = totcop
    }

    setTotalCoop(component){
        component.state = {amount: this.TotalCoop}
    }
    setTotalSec(component){
        component.state = {amount: this.TotalSec}
}

    

    componentDidMount(){
        const mapboxLogo = document.querySelector("div.mapbox.mapboxgl-map > div.mapboxgl-control-container > div.mapboxgl-ctrl-bottom-left > div > a")
        mapboxLogo.style.display = "none"
        const mapboxLiks = document.querySelector("div.mapbox.mapboxgl-map > div.mapboxgl-control-container > div.mapboxgl-ctrl-bottom-right > div")
        mapboxLiks.style.display = "none"
    }
    render(){
        return <div>
            <Navbar></Navbar>
            <TopStatistiCView>
                <StatisticCard
                    titleVar = "Secteurs"
                    className = "alert alert-success"
                    icon = {<i className="fas fa-dice-d20"></i>}
                    text="Le totale des secteurs occupés par l'Anoc"
                    callback = {this.setTotalSec}
                />
                <StatisticCard
                    titleVar = "Coopératives"
                    className = "alert alert-info"
                    icon = {<i className="fas fa-hands tracicon"></i>}
                    text="le total des coopératives de l'Anoc  au niveaux du maroc"
                    callback = {this.setTotalCoop}
                />
                <StatisticCard
                    titleVar = "Especes"
                    className = "alert alert-secondary"
                    text="Le totale des Especes disponibles"
                    icon = {<img className="svg"src = "cow.svg" alt="cow"/>}
                    callback = {this.calculOfTotalEspece}
                />
                <StatisticCard
                    titleVar = "Eleveurs"
                    className = "alert alert-warning"
                    text="Le totale des élveurs adhèrants aux coopératives de l'Anoc"
                    icon = {<img className="svg"src = "gardener.svg" alt="cow"/>}
                    callback = {this.calculOfTotalEleveur}
                />
                <StatisticCard
                    titleVar = "Animateurs"
                    className = "alert alert-primary"
                    icon = {<i className="fas fa-user-tie"></i>}
                    text="Le totale des Animateux de coopératives"
                    callback = {this.calculOfTotalAnimateur}
                />
                <StatisticCard
                    titleVar = "Techniciens"
                    className = "alert alert-danger"
                    icon = {<i className="fas fa-vest-patches"></i>}
                    text="Le totale des Techniciens de coopératives"
                    callback = {this.calculOfTotalTechniciens}
                />
                <StatisticCard
                    titleVar = "Consommateurs"
                    className = "alert alert-dark"
                    icon = {<i className="fas fa-user-tag"></i>}
                    text="Le totale des Consommateurs dans l'ANOC"
                    callback = {this.calculOfTotalConsommateurs}
                />
            </TopStatistiCView>
            <AnnocReactMap 
                coopData = {this.props.coopData}
                currentSecteur = {this.state.currentSecteur}
                currentSelectedCooperative = {this.state.currentSelectedCooperative}
                onSelectedSecteurChange = {this.onSelectedSecteurChange}
                onSelectedCooperativeChange = {this.onSelectedCooperativeChange} />
            <TopStatistiCView>
            <CooperativeStatistics 
                currentSelectedCooperative = {this.state.currentSelectedCooperative}
                currentSecteur = {this.state.currentSecteur}/>
            </TopStatistiCView>
        </div>
    }
}

export default AnnocRootApp
