import React from 'react'

class SecteurSelector extends React.Component{
    constructor(props){
      super(props)
      this.state = {currentSecteur: this.props.currentSecteur}
      this.handleSelectChange = this.handleSelectChange.bind(this)
    }
    handleSelectChange(e){
      e.preventDefault()
      this.setState({currentSecteur: e.target.value})
      this.props.onSelectedSecteurChange(e.target.value)
      this.props.onSelectedCooperativeChange(null)
    }
    render(){
        let options = []
        this.props.coopData.map((secteur, index)=>{
          options.push(<option key = {index} value={index}>{secteur.nom_secteur}</option>)
        return null })
        return <select 
                className="form-select my-2 bg-primary text-light" 
                aria-label="Default select example"
                value = {this.state.currentSecteur}
                onChange = {this.handleSelectChange} >
                  <option value={-1} >Tout les secteurs</option>
                  {options}
            </select>
    }
}

export default SecteurSelector
