import React from 'react'
import { CircularProgressbarWithChildren , buildStyles } from 'react-circular-progressbar';

class CircularStatView extends React.Component {
    render() {
        return <div style={{ width: 150, height: 150, margin: 10 }}>
            <CircularProgressbarWithChildren value={this.props.percentage}
                styles={buildStyles({
                    strokeLinecap: 'butt',
                    textSize: '5px',
                    pathTransitionDuration: 0.5,
                    pathColor: this.props.pathColor,
                    trailColor: this.props.trailColor,
                })}>
                <div style={{ fontSize: 12, marginTop: -5, width: "80%", textAlign:"center"}}>
                    <strong>{this.props.percentage}%</strong> {this.props.title}
                </div>
            </CircularProgressbarWithChildren>
        </div>
    }

}

export default CircularStatView
