import axios from "axios";
import React from "react";
import Spinner from '../Utilities/Spiner';
import CircularStatView  from './CircularStatView';

class CooperativeStatistics extends React.Component {

  constructor(props) {
    super(props)
    this.state = {
      satistics:{}
    }
    this.currentCoop_id = null
    this.dataAvailable= false
  }
  getCooperativeStatistics(id_cooprerative) {
    axios
      .get("http://127.0.0.1:8000/api/dashprovider/statistics/cooperative/" + id_cooprerative)
      .then((res) => {
        this.dataAvailable= true
        this.setState({ satistics: res.data });
      });
  }


  render() {
    //pour envoyé une request au serveur il faut que currentSelectedCooperative soit non null si c'est le cas il aut que
    // la nouvel valeur de currentSelectedCooperative soit différente de currentCoop donc on compare les ids
    if (this.props.currentSelectedCooperative !== null) {
      if (this.props.currentSelectedCooperative._id !== this.currentCoop_id) {
        this.getCooperativeStatistics(this.props.currentSelectedCooperative._id)
        this.dataAvailable = false
        this.currentCoop_id = this.props.currentSelectedCooperative._id
      }
    }
    else{
      this.dataAvailable = false
    }
    return (
      <div className="w-100">
        <div style={{ height: "270px" }} className="m-2  bg-white border p-2">
          <div className="my-2 p-2 bg-light border" >Statistiques de la coopérative: <span className="border py-1 px-3 m-1 bg-secondary text-white" >{this.props.currentSelectedCooperative ? this.props.currentSelectedCooperative.nom : "Aucune"}</span></div>

          {this.dataAvailable ? <React.StrictMode><table className="table">
            <thead className="table-header-bg font_table">
              <tr>
                <th scope="col"></th>
                <th scope="col">Especes</th>
                <th scope="col">Eleveurs</th>
                <th scope="col">Commandes</th>
                <th scope="col">Techniciens</th>
              </tr>
            </thead>
            <tbody>
              <tr>
                <td>Effectif</td>
                <td>{this.state.satistics["espece"]["effectif"]}</td>
                <td>{this.state.satistics["eleveur"]["effectif"]}</td>
                <td>{this.state.satistics["commande"]["percentage"]}</td>
                <td>{this.state.satistics["techniciens"]["percentage"]}</td>
              </tr>
              <tr>
                <td>Pourcentage</td>
                <td>{this.state.satistics["espece"]["percentage"]}%</td>
                <td>{this.state.satistics["eleveur"]["percentage"]}%</td>
                <td>{this.state.satistics["commande"]["percentage"]}%</td>
                <td>{this.state.satistics["techniciens"]["percentage"]}%</td>
              </tr>
            </tbody>
          </table> 
          <p className="text-center small">Tableau : Le nombre d'éléments et leurs pourcentages au niveau de l'ANOC </p>
          </React.StrictMode>: <Spinner state={this.props.currentSelectedCooperative ? true : false} />}
        </div>
        {/**---------------------------------------------------------- */}
        <div style={{ height: "270px" }} className="m-2  bg-white border p-2">
          <div className="my-2 p-2 bg-light border" >Graphiques sectoriels de la coopérative currente: <span className="border py-1 px-3 m-1 bg-secondary text-white" >{this.props.currentSelectedCooperative ? this.props.currentSelectedCooperative.nom : "Aucune"}</span></div>
          {this.dataAvailable ? <React.StrictMode><div className="d-flex flex-row justify-content-between">
            <CircularStatView trailColor ="#e7e7e7" pathColor ="#f76fd9" percentage = {this.state.satistics["espece"]["percentage"]} title ="Des techniciens de l'ANOC"/>
            <CircularStatView trailColor ="#e7e7e7" pathColor ="#e15252" percentage = {this.state.satistics["eleveur"]["percentage"]} title ="des éleveurs de l'ANOC"/>
            <CircularStatView trailColor ="#e7e7e7" pathColor ="#33e970" percentage = {this.state.satistics["commande"]["percentage"]} title ="des commandes de l'ANOC"/>
            <CircularStatView trailColor ="#e7e7e7" pathColor ="#78C4D4" percentage = {this.state.satistics["techniciens"]["percentage"]} title ="d'espèces de l'ANOC"/>
          </div>
          <p className="text-center small p-10">Figure : Repréentations graphiques des pourcentages </p>
          </React.StrictMode>
          : <Spinner state={this.props.currentSelectedCooperative ? true : false}/>
          }
        </div>

      </div>

    );
  }
}

export default CooperativeStatistics;
