import React from 'react'

class CooperativeRow extends React.Component{
  constructor(props){
    super(props)
    this.handleMouseEnter = this.handleMouseEnter.bind(this)
    this.handleMouseLeave = this.handleMouseLeave.bind(this)
    this.handleMouseUp = this.handleMouseUp.bind(this)
  }
  handleMouseEnter(){
    this.props.onHoveredCooperativeChange(this.props.cooperative._id)
  }
  handleMouseLeave(){
    this.props.onHoveredCooperativeChange(null)
  }
  handleMouseUp(){
    this.props.onSelectedCooperativeChange(this.props.cooperative)
    this.props.flyToCooperative(this.props.cooperative.position)
  }
  
  render(){
      return <li className="list-group-item list-group-item-action d-flex flex-row justify-content-between btn"
      onMouseEnter = {this.handleMouseEnter} 
      onMouseLeave = {this.handleMouseLeave}
      onMouseUp = {this.handleMouseUp}>
        {this.props.cooperative.nom} 
        <div className="circle" style = {{backgroundColor: this.props.color}} >{this.props.num}</div>
        </li>
  }
}
//aria-current="true"
export default CooperativeRow
